mapper.select:
SELECT *
FROM reservation rez
         INNER JOIN restaurant rt on rt.RESTAURANT_ID = rez.RESTAURANT_ID
WHERE rez.STATUS = #{status}
  AND cast(rez.TIME as DATE) = #{searchDate}
  AND cast(rez.TIME as TIME) >= #{visitTime};

        SELECT *
        FROM reservation rez
        WHERE rez.RESTAURANT_ID = #{restaurantId}
          AND rez.TIME = #{time};
--             FOR UPDATE;

mapper.update:
UPDATE reservation
SET STATUS = #{status}
WHERE reservation_id = #{reservationId}

mapper.select:
SELECT rez.RESERVATION_ID, rez.MEMBER_ID, rez.TIME, rez.NUMBER_OF_PEOPLE, rez.MEMO, rez.STATUS, rez.CREATED_DATE, rez.UPDATED_DATE, rez.RESTAURANT_ID, rt.OWNER_ID AS RESTAURANT_OWNER_ID, rt.NAME AS RESTAURANT_NAME, rt.CATEGORY AS RESTAURANT_CATEGORY, rt.CONTENT AS RESTAURANT_CONTENT, rt.PHONE AS RESTAURANT_PHONE, rt.TABLE_PERSON_MAX AS RESTAURANT_TABLE_PERSON_MAX, rt.TABLE_PERSON_MIN AS RESTAURANT_TABLE_PERSON_MIN, rt.OPEN_TIME AS RESTAURANT_OPEN_TIME, rt.LAST_ORDER_TIME AS RESTAURANT_LAST_ORDER_TIME, rt.CLOSE_TIME AS RESTAURANT_CLOSE_TIME, rt.ADDRESS AS RESTAURANT_ADDRESS, rt.DETAIL_ADDRESS AS RESTAURANT_DETAIL_ADDRESS, rt.LUNCH_PRICE AS RESTAURANT_LUNCH_PRICE, rt.DINNER_PRICE AS RESTAURANT_DINNER_PRICE, rt.SAVED_COUNT AS RESTAURANT_SAVED_COUNT, rt.REVIEW_COUNT AS RESTAURANT_REVIEW_COUNT, rt.REVIEW_AVG AS RESTAURANT_REVIEW_AVG, rez.PAYMENT_ID, p.IMP_UID AS PAYMENT_IMP_UID, p.PAY_METHOD AS PAYMENT_PAY_METHOD, p.PRICE AS PAYMENT_PRICE, p.STATUS AS PAYMENT_STATUS FROM reservation rez INNER JOIN restaurant rt ON rt.RESTAURANT_ID = rez.RESTAURANT_ID INNER JOIN payment p ON p.PAYMENT_ID = rez.PAYMENT_ID WHERE reservation_id IN (
#{noShowId}
);

mapper.update:
UPDATE reservation
SET STATUS = #{status}
WHERE reservation_id IN (

    #{noShowId}

